      #PositiveDouble is a double
      class PositiveDouble < SimpleType
      
        
        #minExclusive = 0
        def minExclusive(value)
        
          return value > 0
        
        end
        
      
        
      
        def self.valid?(value)
          result = true
        
          
          result = result && self.minExclusive(value)
          
        
          
          
        
